var O2 = require('o2');
var Effect = require('./Abstract/BreakableEffect.js');

// Effet de paralyzie
// L'effet est brisable lorsque la creature subit des dégâts
O2.extendClass('ADV.Effect.Hold', Effect, {
	__construct: function(nStrength) {
		O2.parent(this, 'Hold', 'curse');
		this.setLevel(nStrength);
	},
	
	cast: function(ep) {
		this.getTarget().getSoul().setBonus('hold', 1);
	},

	
	expire: function(ep) {
		this.getTarget().getSoul().setBonus('hold', -1);
	}
});

module.exports = ADV.Effect.Hold;
