var O2 = require('o2');
var Effect = require('./Abstract/Effect.js');

// Bouclier reflecteur
// Tous les missiles qui frappe le lanceurs de sort
// sont réfléchi à 180°
// les effets du missile s'exprime contre le lanceur de sot
// avec une intensité et une durée limitée
// le missile renvoyé est moins puissant.
O2.extendClass('ADV.Effect.Reflect', Effect, {
	
	__construct: function(nPower) {
		O2.parent(this, 'Reflect', 'bless');
		this.setLevel(nPower);
	},
	
	cast: function(ep) {
		this.getTarget().getSoul().setBonus('reflect', this.getLevel());
	},

	
	expire: function(ep) {
		this.getTarget().getSoul().setBonus('reflect', -this.getLevel());
	}
});

module.exports = ADV.Effect.Reflect;
