var O2 = require('o2');
var Effect = require('./Abstract/Effect.js');

// Effet de modification de la vitesse de d√©placement
//la valeur de l'effet s'exprime en pourcentage
O2.extendClass('ADV.Effect.Speed', Effect, {
	
	__construct: function(value) {
		O2.parent(this, 'Speed', value < 0 ? 'curse' : 'bless');
		this.setLevel(value);
	},
	
	cast: function(ep) {
		this.getTarget().getSoul().setBonus('speed', this.getLevel());
	},

	
	expire: function(ep) {
		this.getTarget().getSoul().setBonus('speed', -this.getLevel());
	}
});

module.exports = ADV.Effect.Speed;
