var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Book = require('./Abstract/Book.js');
var u = require('../Utils.js');

/**
 * spellbook of clairvoyance
 * Ajoute sur la minimap la position des adversaire et des missiles.
 */
var BookClairvoyance = O2.extendClass(Book, {
	cooldown: CONST.SPELL_DURATION_LEVEL[2],

	read: function(ctx) {
		var eCV = new Effect.Clairvoyance();
		eCV.setDuration(CONST.EFFECT_DURATION_MEDIUM);
		this.bless(eCV);
	}
});

module.exports = BookClairvoyance;
