var O2 = require('o2');
var u = require('../Utils.js');
var Effect = require('../Effects.js');
var Potion = require('./Abstract/Potion.js');
var CONST = require('../data/consts.js');

/**
 * Potion de Confusion
 * inflige confusion a celui qui boit cette potion.
 */
O2.extendClass('PotionConfusion', Potion, {
	drink: function(ctx) {
		var eConf = new Effect.Confusion();
		eConf.setDuration(CONST.EFFECT_DURATION_SHORT);
		ctx.effects = [eConf];
	}
});

module.exports = PotionConfusion;
