var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Potion = require('./Abstract/Potion.js');

/**
 * Potion de résistance 
 * la puissance des effets négatifs immédiat ou permanant appliqués est reduites de 25%
 * la durée des effets négatifs temporaires est reduite de 25%
 */
var PotionResist = O2.extendClass(Potion, {
	drink: function(ctx) {
		var eDef = new Effect.Defense(25);
		eDef.setDuration(CONST.EFFECT_DURATION_LONG);
		ctx.effects = [eDef];
	}
});

module.exports = PotionResist;

