var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Scroll = require('./Abstract/Scroll.js');

/**
 * Parchemin de cécité :
 * La cible ne peut plus rien voir.
 */
O2.extendClass('ScrollBlind', Scroll, {
	read: function(ctx) {
		var eBlind = new Effect.Blindness();
		eBlind.setDuration(CONST.EFFECT_DURATION_SHORT);
		this.curse(eBlind);
	}
});

module.exports = ScrollBlind;
