var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Scroll = require('./Abstract/Scroll.js');
var u = require('../Utils.js');

/**
 * Parchemin de paralysie:
 * La cible visée par ce sort est paralysiée et ne peut plus effectuer aucune action, ni déplacement, ni attaque.
 * Le sort peut être brisé et la cible libérée si celle-ci est frappée par des projectiles.
 * (entre 3 et 6 projectile suffisent à briser le sort).
 */
O2.extendClass('ScrollHold', Scroll, {
	read: function(ctx) {
		var eHold = new Effect.Hold(u.rand(3, 6));
		eHold.setDuration(CONST.EFFECT_DURATION_SHORT);
		this.curse(eHold);
	}
});

module.exports = ScrollHold;
