var O2 = require('o2');
var CONST = require('../data/consts.js');
var Effect = require('../Effects.js');
var Scroll = require('./Abstract/Scroll.js');

/**
 * Parchemin de lenteur :
 * reduit la vitesse de d√©placement de 33%.
 * et la cadence de tir de 40%
 */
var ScrollSnare = O2.extendClass(Scroll, {
	read: function(ctx) {
		var eSlow, eCD;
		eSlow = new Effect.Speed(-33);
		eSlow.setDuration(CONST.EFFECT_DURATION_SHORT);
		eCD = new Effect.Cooldown(40);
		eCD.setDuration(CONST.EFFECT_DURATION_SHORT);
		this.curse([eSlow, eCD]);
	}
});

module.exports = ScrollSnare;
